@import url("https://fonts.googleapis.com/css2?family=Montserrat:wght@600&display=swap");

@import "variables";

*,
*::before,
*::after {
  box-sizing: border-box;
}

body {
  font-family: "Montserrat", sans-serif;
  font-style: normal;
  font-weight: 600;
}

.main {
  width: 97%;
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
}

.header {
  &__title {
    font-size: 1.5rem;
    line-height: 1.81rem;

    color: $light-black;
  }
}

.checkout {
  background: $gray-6;
  padding: 0 1.5em;
  border-radius: 0.8rem;

  &__items {
    display: flex;
    flex-direction: column;

    .item {
      margin-top: 1.875rem;
      display: grid;
      grid-template-columns: 1fr 1fr;
      grid-template-rows: 1fr 1fr;
      grid-gap: 1em;
      grid-template-areas:
        "item__img item__info"
        "item__img item__qty";

      &__img {
        grid-area: item__img;

        img {
          width: 100%;
          height: 100%;
          max-width: 100%;
          object-fit: cover;
          border-radius: 0.8rem;
        }
      }

      &__info {
        grid-area: item__info;

        .item__name {
          margin-top: 0;
          font-size: 0.75rem;
          line-height: 0.94rem;
          color: $light-black;
        }

        .item__price {
          display: flex;
          align-items: center;
          gap: 1em;

          &--sale {
            font-size: 0.875rem;
            line-height: 1.06rem;
            color: #f2994a;
          }

          &--original {
            font-size: 0.625rem;
            line-height: 0.75rem;
            text-decoration: line-through;
            color: $light-black;
          }
        }
      }

      &__qty {
        grid-area: item__qty;

        display: inline-flex;
        align-items: flex-end;

        .controls {
          display: flex;
          padding: 1em 0.5em;
          border: 1px solid $gray-3;
          border-radius: 0.75rem;
        }

        input[type="number"] {
          -webkit-appearance: textfield;
          -moz-appearance: textfield;
          appearance: textfield;
        }

        input[type="number"]::-webkit-inner-spin-button,
        input[type="number"]::-webkit-outer-spin-button {
          -webkit-appearance: none;
        }

        button {
          outline: none;
          -webkit-appearance: none;
          background-color: $gray-5;
          border: none;
          display: flex;
          align-items: center;
          justify-content: center;
          width: 1.2rem;
          height: 1.2rem;
          cursor: pointer;
          margin: 0;
          font-size: 1rem;
          position: relative;
          border-radius: 0.25rem;
          color: #828282;
        }

        input[type="number"] {
          font-size: 1rem;
          line-height: 1.25rem;
          width: 100%;
          height: 1.2rem;
          margin: 0 auto;
          text-align: center;
          border: none;
          background: inherit;
          color: $gray-1;

          &:focus {
            outline: none;
          }
        }
      }
    }
  }

  &__value {
    margin-top: 5.625rem;

    .row {
      display: flex;
      gap: 1em;
      justify-content: space-between;
      border-top: 1px solid $gray-4;
    }

    .value__type,
    .value__price {
      font-size: 0.875rem;
      line-height: 1.0625rem;
      color: $gray-1;
    }

    &--ship {
      padding: 0.5em 0;
    }

    &--total {
      padding: 1em 0;
    }
  }
}

.form {
  display: flex;
  flex-direction: column;

  section {
    margin-top: 2em;

    h2 {
      font-size: 0.875rem;
      line-height: 1.0625rem;
      color: $gray-1;
      margin-bottom: 0.75rem;
    }
  }

  &__list {
    margin: 0;
    padding: 0;
    list-style: none;

    &--item {
      margin-top: 1em;
      display: flex;
      flex-direction: column;

      label {
        font-size: 0.625rem;
        line-height: 0.75rem;
        color: $gray-2;
      }

      .inputWithIcon {
        display: flex;
        align-items: center;

        .material-icons {
          font-size: 1rem;
          position: absolute;
          margin: 0.1em 0 0 0.5em;
          z-index: 1;
          color: $gray-3;
        }

        input,
        select {
          width: 100%;
          position: relative;
          margin-top: 0.5em;
          padding: 0.75em 0.5em 0.75rem 2rem;
          border: 1px solid $gray-3;
          border-radius: 0.75rem;
          font-size: 0.75rem;
          line-height: 0.94rem;

          &::placeholder {
            color: $gray-3;
          }
        }

        select:invalid {
          color: $gray-3;
        }
      }
    }
    .list--item-row {
      display: grid;
      grid-template-columns: 1fr 1fr;
      grid-gap: 1.25em;

      .item-row-1 {
        grid-column: 1 / 2;
      }

      .item-row-2 {
        grid-column: 2 / -1;
      }

      input[type="number"] {
        line-height: 1rem;
      }
    }
  }

  .check--save {
    font-size: 0.625rem;
    line-height: 0.75rem;
    margin: 1em 0 1.3em 0;
    display: flex;
    align-items: center;

    .checkBox {
      margin-left: 0;
    }
  }

  .submit--btn {
    font-size: 1rem;
    line-height: 1.25rem;
    width: 10.3em;
    padding: 1em 0;
    color: #ffffff;
    background: $orange;
    border: none;
    border-radius: 0.75rem;
    align-self: flex-end;
  }
}

.footer {
  text-align: center;
  color: #a9a9a9;
  margin-top: 3em;
  font-size: 0.8rem;
}

@import "responsive";

